const { chromium } = require('playwright');

async function scanSite(url, name) {
  const browser = await chromium.launch({ headless: true });
  const page = await browser.newPage();

  console.log(`\n${'='.repeat(60)}`);
  console.log(`ðŸ“ ${name}: ${url}`);
  console.log('='.repeat(60));

  try {
    await page.goto(url, { waitUntil: 'networkidle', timeout: 30000 });

    // Scroll to bottom to load all content
    await page.evaluate(async () => {
      await new Promise((resolve) => {
        let totalHeight = 0;
        const distance = 500;
        const timer = setInterval(() => {
          window.scrollBy(0, distance);
          totalHeight += distance;
          if (totalHeight >= document.body.scrollHeight) {
            clearInterval(timer);
            resolve();
          }
        }, 100);
        // Safety timeout
        setTimeout(() => { clearInterval(timer); resolve(); }, 10000);
      });
    });

    // Wait a bit for lazy-loaded content
    await page.waitForTimeout(2000);

    // Get page title
    const title = await page.title();
    console.log(`\nðŸ“Œ Title: ${title}`);

    // Get meta description
    const metaDesc = await page.$eval('meta[name="description"]', el => el.content).catch(() => 'N/A');
    console.log(`ðŸ“ Meta: ${metaDesc}`);

    // Get all text content
    const textContent = await page.evaluate(() => {
      // Remove scripts and styles
      const clone = document.body.cloneNode(true);
      clone.querySelectorAll('script, style, noscript').forEach(el => el.remove());
      return clone.innerText;
    });

    // Clean and print
    const cleanText = textContent
      .split('\n')
      .map(line => line.trim())
      .filter(line => line.length > 0)
      .join('\n');

    console.log(`\nðŸ“„ CONTEÃšDO:\n${cleanText.substring(0, 8000)}`);
    if (cleanText.length > 8000) console.log(`\n... [truncado, total: ${cleanText.length} chars]`);

  } catch (e) {
    console.log(`âŒ Erro: ${e.message}`);
  }

  await browser.close();
}

(async () => {
  await scanSite('https://mentoringbase.com', 'MentoringBase');
  await scanSite('https://iavancada.com', 'IAVancada');
  await scanSite('https://agilcontratos.inteligenciaavancada.com', 'Agil Contratos');
})();
EOF
